create database CLDV_ICE2_16012775

create table ROUTE(ROUTE_ID VARCHAR(5) NOT NULL PRIMARY KEY,ROUTE_NAME VARCHAR(40) NOT NULL,ROUTE_DISTANCE VARCHAR(40) NOT NULL,ROUTE_TIME VARCHAR(40) NOT NULL,)create table LOCATION(LOCATION_ID VARCHAR(5) NOT NULL PRIMARY KEY,LOCATION_NAME VARCHAR(40) NOT NULL,LOCATION_ORDERS VARCHAR(40) NOT NULL,)create table CONTAINER(CONTAINER_ID VARCHAR(5) NOT NULL PRIMARY KEY,CONTAINER_TYPE VARCHAR(40) NOT NULL,CONTAINER_COLOUR VARCHAR(40) NOT NULL,CONTAINER_SIZE VARCHAR(40) NOT NULL,)create table ROUTE_PATHS(ROUTELINKS_ID VARCHAR(5) NOT NULL PRIMARY KEY,ROUTE_ID VARCHAR(5) NOT NULL,LOCATION_ID VARCHAR(5) NOT NULL,FOREIGN KEY (ROUTE_ID) REFERENCES ROUTE(ROUTE_ID),FOREIGN KEY (LOCATION_ID) REFERENCES LOCATION(LOCATION_ID),)create table DOCUMENTS(DOCUMENT_ID VARCHAR(5) NOT NULL PRIMARY KEY,DOCUMENT_NAME VARCHAR(40) NOT NULL,DOCUMENT_TYPE VARCHAR(40) NOT NULL,DOCUMENT_DESCRIPTION VARCHAR(40) NOT NULL,)create table DOCUMENTS_SETS(DOCUMENT_SET_ID VARCHAR(5) NOT NULL PRIMARY KEY,LOCATION_ID VARCHAR(5) NOT NULL,FOREIGN KEY (LOCATION_ID) REFERENCES LOCATION(LOCATION_ID),)create table DOCUMENT_PATHS(LINK_ID VARCHAR(5) NOT NULL PRIMARY KEY,DOCUMENT_SET_ID VARCHAR(5) NOT NULL,DOCUMENT_ID VARCHAR(5) NOT NULL,DOCUMENT_UPLOAD VARCHAR(40) NOT NULL,FOREIGN KEY (DOCUMENT_SET_ID) REFERENCES DOCUMENTS_SETS(DOCUMENT_SET_ID),FOREIGN KEY (DOCUMENT_ID) REFERENCES DOCUMENTS(DOCUMENT_ID),)create table SHIPMENT(SHIPMENT_ID VARCHAR(5) NOT NULL PRIMARY KEY,START_DATE VARCHAR(40) NOT NULL,COLOUR VARCHAR(40) NOT NULL,SIZE VARCHAR(40) NOT NULL,)create table CUSTOMER(CUSTOMER_ID VARCHAR(5) NOT NULL PRIMARY KEY,CUSTOMER_NAME VARCHAR(40) NOT NULL,CUSTOMER_LOCATION VARCHAR(40) NOT NULL,)create table LOCATION_LOGS(LOG_ID VARCHAR(5) NOT NULL PRIMARY KEY,DATE_ARRIVAL VARCHAR(40) NOT NULL,DATE_DEPARTURE VARCHAR(40) NOT NULL,CONTAINER_ID VARCHAR(5) NOT NULL,POINT_FORM VARCHAR(40) NOT NULL,ROUTE_ID VARCHAR(5) NOT NULL,LOCATION_ID VARCHAR(5) NOT NULL,CUSTOMER_ID VARCHAR(5) NOT NULL,POINT_START VARCHAR(40) NOT NULL,POINT_END VARCHAR(40) NOT NULL,FOREIGN KEY (CONTAINER_ID) REFERENCES CONTAINER(CONTAINER_ID),FOREIGN KEY (ROUTE_ID) REFERENCES ROUTE(ROUTE_ID),FOREIGN KEY (LOCATION_ID) REFERENCES LOCATION(LOCATION_ID),FOREIGN KEY (CUSTOMER_ID) REFERENCES CUSTOMER(CUSTOMER_ID),)